import React, { useContext, useState, useEffect } from 'react';
import { connect, useDispatch } from 'react-redux'; // Import connect from react-redux
import CartRow from './CartRow'; // Assuming you have a CartRow component
import PropTypes from 'prop-types';
import { ShopContext } from '../Context/ShopContext';
import './Css/Cart.css'
import { createOrder, generateOrderId } from '../Reducer/orderReducer';
import { useNavigate } from 'react-router-dom';
import { CartTypes } from '../Reducer/cartReducer';
import Popular from '../Components/Popular/Popular';

function Cart({ cart, items, order }) {
  const [name, setName] = useState('');
  const [creditCardNumber, setCreditCardNumber] = useState('');
  const [street, setStreet] = useState('');
  const [city, setCity] = useState('');
  const [states, setStates] = useState('');
  const [orderType, setOrderTpye] = useState('Home delivery');
  const [instorePickup, setInstorePickup] = useState('--------------------');
  const [phone, setPhone] = useState('');
  const [zipCode, setZipCode] = useState('');
  const {all_product}=useContext(ShopContext);
  const dispatch= useDispatch();
  const navigate  = useNavigate ();

  const cartAccessories = cart.reduce((acc, item) => {
    const detailItem = all_product.find((i) => i.id === item.id);
    if (detailItem && detailItem.accessories) {
      acc.push(...detailItem.accessories);
    }
    return acc;
  }, []);

  // const subTotal = cart.reduce((acc, item) => {
  //   const detailItem = all_product.find((i) => i.id === item.id);
  //   const itemPrice = detailItem.salePrice ?? detailItem.price;
  //   return item.quantity * itemPrice + acc;
  // }, 0);
  const subTotal = cart.reduce((acc, item) => {
    const detailItem = all_product.find((i) => i.id === item.id);
  
    if (detailItem) {
      const itemPrice = detailItem.salePrice ?? detailItem.price;
      return item.quantity * itemPrice + acc;
    }
  
    return acc;
  }, 0);
  const orderDetails = cart.map((item) => ({
    productId: item.id, // Assuming 'id' represents the productId
    orderPrice: item.price, // Assuming 'orderPrice' is the price of the item in the cart
  }));

  const taxPercentage = parseInt(zipCode.substring(0, 1) || '0', 10) + 1;
  const taxRate = taxPercentage / 100;
  const tax = subTotal * taxRate;
  const total = subTotal + tax;
  const isFormValid = zipCode.length === 5 && name.trim();
  const discount = 40.0;
  
 
  
    const [today, setToday] = useState(new Date());
    const purchaseDate = `${today.getFullYear()}-${(today.getMonth() + 1)
      .toString()
      .padStart(2, '0')}-${today.getDate().toString().padStart(2, '0')}`;
    const futureDate = new Date(today);
    futureDate.setDate(futureDate.getDate() + 14);
    setToday(futureDate);
  const storeCity='Chicago';
  const storeState = 'IL';
  const storeZip ='60645';


  const submitOrder = async (event) => {
    event.preventDefault();
  
    try {
      const response = await fetch('http://localhost:3001/storeOrder', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          orderId: generateOrderId(order),
          name,
          creditCardNumber,
          street,
          city,
          states,
          zipCode,
          orderType,
          orderDetails: cart.map((item) => ({
            productId: item.id,
            quantity: item.quantity
          })),
          instorePickup,
          subTotal,
          discount,
          purchaseDate,
          futureDate,
          storeCity,
          storeState,
          storeZip

        }),
      });
      if (response.ok) {
        console.log('Order submitted successfully');
        // ... other code ...
      } else {
        console.error('Error submitting order:', response.statusText);
      }
      // Rest of your code for handling the response...
    } catch (error) {
      console.error('Error placing order:', error);
      // Handle error
    }
  };
  
  

  const setFormattedPhone = (newNumber) => {
    const digits = newNumber.replace(/\D/g, '');
    let formatted = digits.substring(0, 3);
    if (digits.length === 3 && newNumber[3] === '-') {
      formatted = `${formatted}-`;
    } else if (digits.length > 3) {
      formatted = `${formatted}-${digits.substring(3, 6)}`;
    }
    if (digits.length === 6 && newNumber[7] === '-') {
      formatted = `${formatted}-`;
    } else if (digits.length > 6) {
      formatted = `${formatted}-${digits.substring(6, 10)}`;
    }
    setPhone(formatted);
  };

  return (
    <div className="cart-component">
      <h2>Your Cart</h2>
      {cart.length === 0 ? (
        <div>Your cart is empty.</div>
      ) : (
        <>
          <table>
            <thead>
              <tr>
                <th>Quantity</th>
                <th>Item</th>
                <th>Price</th>
              </tr>
            </thead>
            <tbody>
              {cart.map((item) => (
                <CartRow 
                  key={item.id}
                  cartItem={item}
                  items={all_product}
                />
              ))}
            </tbody>
          </table>
          <div>
            Subtotal: $
            {subTotal.toFixed(2)}
          </div>
          <div> 
            <Popular accessories={cartAccessories}/>
          </div>
          { zipCode.length === 5
            ? (
              <>
                <div>
                  Tax: $
                  {tax.toFixed(2)}
                </div>
                <div>
                  Total: $
                  { total.toFixed(2) }
                </div>
              </>
            ) : (
              <div className="warning">Enter ZIP Code to get total</div>
            )}
          <h2>Checkout</h2>
          <form onSubmit={submitOrder}>
          <label htmlFor="creditCardNumber">
              Credit Card Number
              <input
                id="creditCardNumber"
                type="text"
                value={creditCardNumber}
                onChange={(event) => setCreditCardNumber(event.target.value)}
                required
              />
              </label>
            <label htmlFor="name">
             Customer Name
              <input
                id="name"
                type="text"
                value={name}
