import React, { useState } from 'react'
import '../Pages/Css/Cart.css'
import { connect, useDispatch } from 'react-redux';
import { createOrder, deleteOrder, generateOrderId, getMaxOrderId, updateOrder } from '../Reducer/orderReducer';
import { useNavigate } from 'react-router-dom';


const OrderModify = ({orderUpdate, order}) => {
    const [name, setName] = useState('');
    const [orderId, setOrderId] = useState(null);
    const [productName, setProductName] = useState('');
    const [productPrice, setproductPrice] = useState('');
    const [productQuantity, setProductQuantity]=useState('');
    const [creditCardNumber, setCreditCardNumber] = useState('');
    const [street, setStreet] = useState('');
    const [city, setCity] = useState('');
    const [states, setStates] = useState('');
    const [orderType, setOrderTpye] = useState('Home delivery');
    const [instorePickup, setInstorePickup] = useState('--------------------');
    const [errorMessage, setErrorMessage] = useState('');
    const [zipCode, setZipCode] = useState('');
    const dispatch =useDispatch();
    const navigate=useNavigate();
    const purchaseDate="11-21-2023";
    const deliveryDate="12-4-2023";
  const storeCity='Chicago';
  const storeState = 'IL';
  const storeZip ='60645';
  const discount=40.0;
    

    const submitAddOrder= async (event) =>{
        event.preventDefault();

        if(orderUpdate==='add'){
        dispatch(
            createOrder({
               orderId: generateOrderId(order), // You need to implement generateOrderId
              cart: [{id:productName,quantity:productQuantity}], // Provide the cart id
              name,
              creditCardNumber,
              street,
              city,
              states,
              zipCode,
              orderType,
              instorePickup,
            })
          );

          try {
            const response = await fetch('http://localhost:3001/storeOrder', {
              method: 'POST',
              headers: {
                'Content-Type': 'application/json',
              },
              body: JSON.stringify({
                customerName: name,
                creditCardNumber,
                street,
                city,
                state: states,
                zipCode,
                orderType,
                orderDetails: [{id:productName,quantity:productQuantity}],
                instorePickup,
                orderPrice : 20.0,
                discount,
                purchaseDate,
                deliveryDate,
                storeCity,
                storeState,
                storeZip,
      
              }),
            });
            if (response.ok) {
              console.log('Order submitted successfully');
              // ... other code ...
            } else {
              console.error('Error submitting order:', response.statusText);
            }
            // Rest of your code for handling the response...
          } catch (error) {
            console.error('Error placing order:', error);
            // Handle error
          }


          navigate('/orderadded');
        }
        else{
            console.log('getmaxorder',getMaxOrderId(order));
            console.log(orderId);
            console.log('getMaxOrderId(order)',getMaxOrderId(order));
            console.log(orderId<=getMaxOrderId(order))
            const orderId1= Number(orderId);
            if(orderId1<=getMaxOrderId(order)){
            dispatch(
                updateOrder(orderId1,{
                   name,
                   creditCardNumber,
                   street,
                   city,
                   states,
                   zipCode,
                   orderType,
                   instorePickup,
                 } )
            )

            console.log("after updating order", order);
            navigate('/orderupdated');
                }
                else{
                    setErrorMessage(`Order with ID ${orderId} not found.`);
                    console.error(`Order with ID ${orderId} not found.`);
                }
        }

    }

    const submitDeleteOrder =(event)=>{
        event.preventDefault();
        const orderId1= Number(orderId);
        if(orderId1<=getMaxOrderId(order)){
        dispatch(deleteOrder(orderId1));
        navigate('/orderdeleted');
        }
        else{
            setErrorMessage(`Order with ID ${orderId} not found.`);
             console.error(`Order with ID ${orderId} not found.`);
        }
    }

  return (
    orderUpdate==='add'|| orderUpdate==='update'?(
        <div className='cart-component'>
             <h2>{orderUpdate === 'add' ? 'Add Order' : 'Update Order'}</h2>
              <form onSubmit={submitAddOrder}>
                {
                orderUpdate === 'update' ?(
                    <label htmlFor="orderId">
                    Order Id
                      <input
                        id="orderId"
                        type="numeric"
                        value={orderId}
                        onChange={(event) => setOrderId(event.target.value)}
                        required
                      />
                    </label>
                ):(  <><label htmlFor="productName">
                              Product Name
                              <input
                                  id="productName"
                                  type="text"
                                  value={productName}
                                  onChange={(event) => setProductName(event.target.value)}
                                  required />
                          </label><label htmlFor="productQuantity">
                                  Product Quantity
                                  <input
                                      id="productQuantity"
                                      type="numeric"
                                      value={productQuantity}
                                      onChange={(event) => setProductQuantity(event.target.value)}
                                      required />
                              </label>
                              <label htmlFor="productPrice">
                                  Product Price
                                  <input
                                      id="productPrice"
                                      type="numeric"
                                      value={productPrice}
                                      onChange={(event) => setproductPrice(event.target.value)}
                                      required />
                              </label></>)

                }
                <label htmlFor="creditCardNumber">
              Credit Card Number
              <input
                id="creditCardNumber"
                type="text"
                value={creditCardNumber}
                onChange={(event) => setCreditCardNumber(event.target.value)}
                required
              />
              </label>
            <label htmlFor="name">
             Customer Address Name
              <input
                id="name"
                type="text"
                value={name}
                onChange={(event) => setName(event.target.value)}
                required
              />
            </label>
            <label htmlFor="street">
            Street
              <input
                id="street"
                type="text"
                value={street}
                onChange={(event) => setStreet(event.target.value)}
                required
              />
            </label>
            <label htmlFor="city">
            City
              <input
                id="city"
                type="text"
                value={city}
                onChange={(event) => setCity(event.target.value)}
                required
              />
            </label>
            <label htmlFor="states">
            State
              <input
                id="states"
                type="text"
                value={states}
                onChange={(event) => setStates(event.target.value)}
                required
              />
            </label>
            <label htmlFor="zipcode">
              ZIP Code
              <input
                id="zipcode"
                type="text"
                maxLength="5"
                inputMode="numeric"
                value={zipCode}
                onChange={(event) => setZipCode(event.target.value)}
                required
              />
            </label>
            <label htmlFor="orderTpye">
             Order Type
              <select
                id="orderTpye"
                value={orderType}
                onChange={(event) => setOrderTpye(event.target.value)}
                required
              >
                <option value='Home delivery' selected>Home delivery</option><option value='In store pickup'>In store pickup</option>
                </select>
            </label>
            <label htmlFor="instorePickup">
            In-store Pickup Locations
              <select
                id="instorePickup"
                value={instorePickup}
                onChange={(event) => setInstorePickup(event.target.value)}
